import React, { Component} from "react";
import "./tasks-filter.css";

class TasksFilter extends Component{

    state = {
        All: this.props.allTodo,
        Active: this.props.activeTodo,
        Completed: this.props.completedTodo,
    }

    render() {
        const {butEl} = this.props
        return (
            <div>
                <button onClick={this.state[butEl.text]} className={butEl.className}>All</button>
                <button onClick={this.state[butEl.text]} className={butEl.className}>Active</button>
                <button onClick={this.state[butEl.text]} className={butEl.className}>Completed</button>
            </div>

        );
    }
}

export default TasksFilter;

