import React, { Component} from "react";
import { createRoot } from 'react-dom/client';
import './style.css'

import NewTaskForm from "./components/new-task-form";
import TaskList from "./components/task-list/task-list";
import Footer from "./components/footer/footer";

class TodoApp extends Component{
    maxId = 100;

    state = {
        styleLi: [
        ],
        complitedet: [

        ],
        all: [

        ],
        active: [

        ]
    };

    createItem = (label) => {
        return{
            label: label,
            time: 'created 5 minutes ago',
            done: false,
            id: this.maxId+= 1
        }
    }

    deleted = (id) =>{
     this.setState(({styleLi, all, complitedet, active}) => {
         const index = styleLi.findIndex(el => el.id === id);

         const newAll = [ ...styleLi.slice(0, index),
             ...styleLi.slice(index + 1)];

         const newArray = [ ...all.slice(0, index),
             ...all.slice(index + 1)];

         const newCompl = [ ...complitedet.slice(0, index),
             ...complitedet.slice(index + 1)]

         const newActive = [ ...active.slice(0, index),
             ...active.slice(index + 1)]

         return{
             styleLi: newAll,
             all: newArray,
             complitedet: newCompl,
             active: newActive
         }
     })
    }

    toggleProperty(arr, id, propName){
        const index = arr.findIndex(el => el.id === id);

        const oldItem = arr[index];
        const newItem ={ ...oldItem,
            [propName]: !oldItem[propName]};

        return [...arr.slice(0, index),
            newItem,
            ...arr.slice(index + 1)
        ];

    }

    onLabelDone = (id) => {
       this.setState(({styleLi, all, complitedet, active}) => {

           const newArray = this.toggleProperty(all, id, 'done');
           const arrDone = newArray.filter(el => el.done);
           const arrActiv = newArray.filter(el => !el.done);
           if(styleLi === complitedet){
               this.completedTodo()
           }

           if(styleLi === active){
               this.activeTodo()
           }

           return {
               styleLi: newArray,
               all: newArray,
               complitedet: arrDone,
               active: arrActiv
           }
        })
    }


    addItem = (text) => {
       const newItem =  this.createItem(text);
       this.setState(({styleLi, all, complitedet, active}) => {
           const newArr = [...styleLi, newItem];
           const newArray = [...all, newItem];
           const newActive = [...active, newItem]
         if(styleLi !== complitedet){
             return{
                 all: newArray,
                 styleLi: newArr,
                 active: newActive
             }
         }else{
            return{
                all: newArray,
                active: newActive
            }
         }

       })
    };

    allTodo = () => {
       this.setState(({all}) => {
           return  {
               styleLi: [...all]
           }
       })
    };

    activeTodo = () => {
        this.setState(({active}) => {
            return  {
                styleLi: active
            }
        })
    }

    completedTodo = () => {
        this.setState(({complitedet}) => {
           return{
               styleLi: complitedet
           }
        })
    }

    clearCompleted = () => {
      this.setState(({all, styleLi }) => {
         const doneEl = styleLi.filter(el => !el.done);
         const doneAll = all.filter(el => !el.done);
          return {
              styleLi: doneEl,
              all: doneAll,
              complitedet: []
          }
      })
    }

   render() {
       const {styleLi} = this.state

       const doneCount = styleLi.filter(el => !el.done).length
       return(
           <section className="todoapp">
               <NewTaskForm addItem={this.addItem} />
               <TaskList
                   taskli={styleLi}
                   onDelete={this.deleted}
                   onLabelDone={this.onLabelDone}
               />
               <Footer doNe={doneCount}
                       allTodo={this.allTodo}
                       activeTodo={this.activeTodo}
                       completedTodo={this.completedTodo}
                       clearCompleted={this.clearCompleted}
               />
           </section>
       );
   }
}

const container = createRoot(document.getElementById('root'));

container.render(<TodoApp />)
