import React, { Component} from "react";
import { createRoot } from 'react-dom/client';
import './style.css'

import NewTaskForm from "./components/new-task-form";
import TaskList from "./components/task-list/task-list";
import Footer from "./components/footer/footer";

class TodoApp extends Component{
    maxId = 100;

    state = {
        styleLi: [
        ],
        complited: [

        ],
        all: [

        ]
    };

    createItem = (label) => {
        return{
            label: label,
            time: 'created 5 minutes ago',
            done: false,
            id: this.maxId+= 1
        }
    }

    deleted = (id) =>{
     this.setState(({complited, all}) => {
         const index = all.findIndex(el => el.id === id);

         const newCompl = [...complited.slice(0, index),
                           ...complited.slice(index + 1)]
         const newAll = [ ...all.slice(0, index),
             ...all.slice(index + 1)]

         return{
             all: newAll,
             complited: newCompl
         }
     })
    }

    toggleProperty(arr, id, propName){
        const index = arr.findIndex(el => el.id === id);

        const oldItem = arr[index];
        const newItem ={ ...oldItem,
            [propName]: !oldItem[propName]};

        return [...arr.slice(0, index),
            newItem,
            ...arr.slice(index + 1)
        ];

    }

    onLabelDone = (id) => {
       this.setState(({all, complited, styleLi}) => {
           const newArr = this.toggleProperty(all, id, 'done');
           const arrDone = newArr.filter(el => el.done)
           const newComplit = [ ...arrDone]
           return {
               all: newArr,

               complited: newComplit
           }
        })
    }


    addItem = (text) => {
       const newItem =  this.createItem(text);
       this.setState(({all, styleLi}) => {
           const newArr = [...styleLi, newItem];
           const newAll = [...all, newItem]
         return{
             all: newAll
         }
       })
    };

    allTodo = () => {
       this.setState(({styleLi, all}) => {
           return  {
               styleLi: all
           }
       })
    };

    activeTodo = () => {

    }

    completedTodo = () => {
        this.setState(({styleLi, complited}) => {
           return{
               styleLi: complited
           }
        })
    }

   render() {
       const {styleLi} = this.state

       const doneCount = styleLi.filter(el => !el.done).length
       return(
           <section className="todoapp">
               <NewTaskForm addItem={this.addItem} />
               <TaskList
                   taskli={styleLi}
                   onDelete={this.deleted}
                   onLabelDone={this.onLabelDone}
               />
               <Footer doNe={doneCount}
                       allTodo={this.allTodo}
                       activeTodo={this.activeTodo}
                       completedTodo={this.completedTodo}
               />
           </section>
       );
   }
}

const container = createRoot(document.getElementById('root'));

container.render(<TodoApp />)
