import React, { Component } from "react";

class Task extends Component{

     state ={
         edit: this.props.taskEL.elClassName === "editing" ?
             <input type="text" className="edit" defaultValue="Editing task"></input> :
             false,

     }
    render() {
        const {taskEL} = this.props.taskEL

        return(
            <li className={taskEL.elClassName} key={ taskEL.id }>
                <div className="view">
                    <input className="toggle" type="checkbox"></input>
                    <label>
                        <span className="description">{taskEL.text}</span>
                        <span className="created">{taskEL.time}</span>
                    </label>
                    <button className="icon icon-edit"></button>
                    <button className="icon icon-destroy"></button>
                </div>
                {edit}
            </li>
            )
    }
}

export default Task;
