import React, { Component } from "react";

class Task extends Component{

     state ={
       done: false
     }

    onLabelDone = () => {
       this.setState(({done}) => {
          return {
              done: !done
          }
        })
    }

    render() {
        const {label, time, id } = this.props;
        console.log(label)
        let classNAme = 'description';
        if(this.state.done){
            classNAme += ' done'
        }

        return(
            //const { id} =
            <li >
                <div className="view">
                    <input  className="toggle " type="checkbox" onClick={this.onLabelDone}></input>
                    <label>
                        <span className={classNAme}>{label}</span>
                        <span className="created">{-time}</span>
                    </label>
                    <button className="icon icon-edit"></button>
                    <button className="icon icon-destroy" onClick={this.props.onDelete}></button>
                </div>
            </li>
            )
    }
}

export default Task;
