import React, { Component} from "react";
import "./tasks-filter.css";

class TasksFilter extends Component{

    state = {
        All: [this.props.allTodo, 'selected'],
        Active: [this.props.activeTodo, 'selected'],
        Completed: [this.props.completedTodo, 'selected'],
    }

    render() {
        const {butEl} = this.props
        return (
                <button onClick={this.state[butEl.text][0]} className={this.state[butEl.text][1]}>{butEl.text}</button>
        );
    }
}

export default TasksFilter;
