import React from "react";
import "./footer.css"
import TasksFilter from "../tasks-filter/tasks-filter";

const Footer = ({doNe, conditionTodo, clearCompleted}) => {
   const butEL = [
    {text: 'All', className:'selected', id: 'Al'},
    {text: 'Active', className:'selected', id: 'Ac'},
    {text: 'Completed', className:'selected', id: 'Com'}
   ];


    let selected = false

    const elements =  butEL.map(el => {
    return (
        <li key={el.id }>
        <TasksFilter butEl={el}
                     selected={selected}
                     conditionTodo={() => conditionTodo(el.text, selected)}
        />
        </li>
    )
});

return(
   <footer className="footer">
     <span className="todo-count"> {doNe} items left</span>
     <ul className="filters">
       {elements}
     </ul>
     <button onClick={clearCompleted} className="clear-completed">Clear completed</button>
   </footer> 
)
}
//
export default Footer;
